mapper.select:
select id,img_file_name,title,subtitle,price,distance,number,`desc`,city,category,star_total_num,comment_total_num from business
and (title like '%' #{title} '%' or subtitle like '%' #{subtitle} '%' or `desc` like '%' #{desc} '%')
and city = #{city}
and category = #{category}
order by id

mapper.update:
update business b,
(select o.business_id id,sum(o.price) price_num,count(*) order_num from orders o where o.create_time <= #{endTime}
and o.create_time > #{startTime}
group by o.business_id)t
set b.price = b.price + t.price_num,b.number = b.number + t.order_num where b.id = t.id

UPDATE business b,
(SELECT o.business_id id,SUM(c.star) star_num,COUNT(*) comment_num FROM `comment` c, orders o WHERE c.orders_id=o.id AND c.create_time <= #{endTime}
AND c.create_time > #{startTime}
GROUP BY o.business_id)t
SET b.star_total_num = b.star_total_num + t.star_num,b.comment_total_num = b.comment_total_num + t.comment_num WHERE b.id=t.id
